import "./App.css";
import data from "../src/data/babyNamesData.json";
import { useState } from "react";

const App = () => {
  const [names, setNames] = useState(data);
  const renderNameList = () => {
    const updatedNames = names
      .map((name) => ({ ...name, favourite: false }))
      .sort((nameOne, nameTwo) => nameOne.name.localeCompare(nameTwo.name));

    const onClick = (nameObject) => {
      updatedNames.map((updatedName) => {
        if (nameObject.id === updatedName.id) {
          updatedName.favourite = !updatedName.favourite;
          console.log(updatedName)
        }
        return false;
      });
    };
    return (
      <ul>
        {updatedNames.map((nameObject) => (
          <li onClick={() => onClick(nameObject)}>{nameObject.name}</li>
        ))}
      </ul>
    );
  };

  return <div>{renderNameList()}</div>;
};
export default App;
