import "./ChooseSexButton.css";
import "../App.css";

const ChooseSexButton = ({ handleButton, sex }) => {
  let caption;
  let className;
  if (sex === "f") {
    caption = "Female";

  } else if (sex === "m") {
    caption = "Male";

  } else {
    caption = "All";

  }
  return (
    <button
      className={sex === "f" ? "sex-btn female-btn" : "sex-btn male-btn"}
      onClick={() => handleButton(sex)}
    >
      {caption}
    </button>
  );
};

export default ChooseSexButton;
