import { useState } from "react";

const BabyList = ({ names }) => {
  const [updatedNames, setUpdatedNames] = useState(names);
  const onClick = (nameObject) => {
    const newNames = updatedNames.map((updatedName) => {
      if (nameObject.id === updatedName.id) {
        updatedName.favourite = !updatedName.favourite;
      }
      return updatedName;
    });
    console.log()
    setUpdatedNames(newNames);
  };
  console.log(updatedNames);
  return (
    <ul>
      {updatedNames.map((nameObject) => (
        <li
          onClick={() => onClick(nameObject)}
          className={nameObject.favourite === true ? "favourite-name" : ""}
        >
          {nameObject.name}
        </li>
      ))}
    </ul>
  );
};

export default BabyList;
