      }
      return updatedName;
    });
    setUpdatedNames(newNames);
  };
  console.log(updatedNames);
  return (
    <ul>
      {updatedNames.map((nameObject) => (
        <li
          onClick={() => onClick(nameObject)}
          className={nameObject.favourite === true ? "favourite-name" : ""}
