import "./NameList.css";

const NameList = ({ sortedNames, searchInput, handleClick }) => {
  const filteredNames = sortedNames.filter((entry) => {
    return entry.name.toLowerCase().includes(searchInput.toLowerCase());
  });

  return (
    <ul className="name-list-container">
      {filteredNames.map((nameObj) => (
        <li
          onClick={() => handleClick(nameObj)}
          className={
            nameObj.sex === "f" ? "girl-name name-card" : "boy-name name-card"
          }
        >
          {nameObj.name}
        </li>
      ))}
    </ul>
  );
};

export default NameList;
