import { useState } from "react";
import "./App.css";
import data from "./data/babyNamesData.json";
import NameList from "./Components/NameList";
import Search from "./Components/Search";

const sortNames = data
  .map((name) => ({ ...name, favourite: false }))
  .sort((nameOne, nameTwo) => nameOne.name.localeCompare(nameTwo.name));

const App = () => {
  const [searchInput, setSearchInput] = useState("");
  const [sortedNames, setSortedNames] = useState(sortNames);
  const [filteredNames, setFilterNames] = useState(test);
  // const [favourite, setFavourites] = useState(false);

  test = sortedNames.filter((entry) => {
    return entry.name.toLowerCase().includes(searchInput.toLowerCase());
  });

  const handleClick = (nameObj) => {

    const updatedNames = sortedNames.map((name) => {
      if (name.id === nameObj.id) {
        name.favourite = !name.favourite;
        return { ...name };
      }
      return name;
    });
    setSortedNames(updatedNames);
  };

  const favouriteNames = sortedNames.filter((name) => name.favourite === true);

  return (
    <div className="App">
      <Search searchInput={searchInput} setSearchInput={setSearchInput} />
      <NameList handleClick={handleClick} names={favouriteNames} />
      <NameList handleClick={handleClick} names={filteredNames} />
    </div>
  );
};
export default App;
